sp_widget.client_script:
function($scope, $rootScope, $timeout, spUtil) {
  /* widget controller */
  var c = this;
	var isLoaded = false;

	c.optionSelected = function() {
		var dt = c.options.broadcast_name;
		console.log('dt: ' + JSON.stringify(c.options.e_widget));
		data = {
			'broadcast_name' : dt,
			'e_widget' : c.options.e_widget
		};

		$timeout(function(){
			$rootScope.$broadcast('mu_icon_trigger', data);
		},500);
	};




	$scope.onLoadHandler = function() {
		if(!isLoaded && c.options.on_load == 'true') {
			c.optionSelected();
			isLoaded = true;
		}
	}

}

