sp_widget.client_script:
function($scope, spUtil, $location, spModal, spAriaUtil, $http, $window) {
  /* widget controller */
  var c = this;

	c.createTrigger = function() {
		c.server.get({
			action: "show_form"
		}).then(function(response) {
			var formModalCtrl;
			var unregister = $scope.$on('$sp.submitted', function() {
				formModalCtrl.close();
			});

			var unregisterOnCancel = $scope.$on('$sp.cancelled', function() {
				formModalCtrl.close();
			});

			var formModal = angular.copy(response.data.formModal);
			formModal.options.afterOpen = function(ctrl){
				formModalCtrl = ctrl;
			};
			formModal.options.afterClose = function() {
				unregister();
				c.formModal = null;
				formModalCtrl = null;
			};
			c.formModal = formModal;
		});
	}

}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	if(input && input.action === "show_form") {
		data.formModal = $sp.getWidget('widget-sc-cat-item-v2', {sys_id : '84770ba8db28e740a99f70bdae961925'});
		return;
	}

})();
